body.script:
//
document.addEventListener('DOMContentLoaded', function() {
    // Icon Parser
    const parseIcons = () => {
        // Header Menu - Fixed structure for mobile/desktop
        document.querySelectorAll('.js-menu-item').forEach(el => {
            const text = el.innerText || el.textContent;
            if(text && text.includes('|')) {
                const parts = text.split('|');
                if(parts.length >= 2) {
                    const iconClass = parts[0].trim();
                    const label = parts.slice(1).join('|').trim();
                    // Safely update specific nodes to prevent event listener loss (though less critical here)
                    el.innerHTML = `<i class="${iconClass}"></i> ${label}`;
                }
            }
        });

        // Features - Targeted update
        const featureItems = document.querySelectorAll('.js-feature-item');
        if (featureItems.length > 0) {
            featureItems.forEach(el => {
                const titleEl = el.querySelector('.feature-title');
                if(titleEl) {
                    const text = titleEl.innerText || titleEl.textContent;
                    if(text && text.includes('|')) {
                        const parts = text.split('|');
                        if(parts.length >= 2) {
                            const iconClass = parts[0].trim();
                            const label = parts.slice(1).join('|').trim();

                            // Update Icon
                            const iconI = el.querySelector('.feature-icon i');
                            if(iconI) {
                                iconI.className = iconClass;
                            } else {
                                // Fallback: create icon if missing
                                const iconDiv = el.querySelector('.feature-icon');
                                if(iconDiv) iconDiv.innerHTML = `<i class="${iconClass}"></i>`;
                            }

                            // Update Title
                            titleEl.textContent = label;
                        }
                    }
                }
            });
        }
    };
    // Run immediately and after a short delay to handle potential widget rendering delays
    parseIcons();
    setTimeout(parseIcons, 500);

    const scrollToTopBtn = document.getElementById("scrollToTopBtn");
    const header = document.querySelector(".site-header");
    const heroSection = document.getElementById('hero-section');
    const parallaxImages = document.querySelectorAll('.post-card .post-card-image');
    let lastScrollTop = 0;

    const handleScroll = () => {
        const scrollPosition = window.pageYOffset || document.documentElement.scrollTop;

        // Back to top
        if (scrollPosition > 300) scrollToTopBtn?.classList.add("show");
        else scrollToTopBtn?.classList.remove("show");

        // Header hide/show on scroll
        if (header) {
            if (scrollPosition > lastScrollTop && scrollPosition > 100) {
                header.classList.add("header-hidden");
            } else {
                header.classList.remove("header-hidden");
            }
        }
        lastScrollTop = scrollPosition <= 0 ? 0 : scrollPosition;

        if(heroSection) heroSection.style.backgroundPositionY = scrollPosition * 0.5 + 'px';

        window.requestAnimationFrame(() => {
            const viewportCenter = window.innerHeight / 2;
            parallaxImages.forEach(image => {
                const rect = image.parentElement.getBoundingClientRect();
                if (rect.bottom >= 0 && rect.top <= window.innerHeight) {
                    const elCenter = rect.top + rect.height / 2;
                    const moveY = (elCenter - viewportCenter) * 0.1 * -1;
                    image.style.transform = `scale(1.15) translateY(${moveY}px)`;
                }
            });
        });
    };

    window.addEventListener('scroll', handleScroll, { passive: true });
    scrollToTopBtn?.addEventListener('click', () => window.scrollTo({top: 0, behavior: 'smooth'}));

    // Multi-level Menu Logic (3 levels)
    const navList = document.getElementById('navigation-list');
    if (navList) {
        const items = navList.querySelectorAll('li');
        let level1 = null;
        let level2 = null;

        items.forEach(li => {
            const a = li.querySelector('a');
            if (a) {
                const text = a.textContent.trim();
                if (text.startsWith('__')) { // Level 3
                    a.textContent = text.replace(/^__/, '');
                    if (level2) {
                        let ul = level2.querySelector('ul');
                        if (!ul) {
                            ul = document.createElement('ul');
                            ul.className = 'sub-dropdown-menu';
                            level2.appendChild(ul);
                            level2.classList.add('has-sub-child');
                        }
                        ul.appendChild(li);
                    }
                } else if (text.startsWith('_')) { // Level 2
                    a.textContent = text.replace(/^_/, '');
                    if (level1) {
                        let ul = level1.querySelector('ul');
                        if (!ul) {
                            ul = document.createElement('ul');
                            ul.className = 'dropdown-menu';
                            level1.appendChild(ul);
                            level1.classList.add('has-child');
                        }
                        ul.appendChild(li);
                        level2 = li;
                    }
                } else { // Level 1
                    level1 = li;
                    level2 = null;
                }
            }
        });

        // Handle submenu overflow detection
        const subMenuParents = navList.querySelectorAll('.has-child, .has-sub-child');
        subMenuParents.forEach(li => {
            li.addEventListener('mouseenter', function() {
                const menu = this.querySelector('ul');
                if (menu) {
                    menu.style.display = 'block'; // Temporarily show to calculate
                    const rect = menu.getBoundingClientRect();
                    menu.style.display = ''; // Reset display

                    if (rect.right > (window.innerWidth || document.documentElement.clientWidth)) {
                        menu.classList.add('open-left');
                    } else {
                        menu.classList.remove('open-left');
                    }
                }
            });
        });
    }

    const navToggle = document.getElementById('mobile-nav-toggle');
    const mainNav = document.getElementById('main-nav');
    const overlay = document.getElementById('page-overlay');

    if (navToggle && mainNav && overlay) {
        navToggle.onclick = () => {
            mainNav.classList.add('mobile-open');
            setTimeout(() => {
                mainNav.classList.add('is-active');
                overlay.classList.add('is-active');
                document.body.classList.add('no-scroll');
            }, 10);
        };

        const closeNav = () => {
            mainNav.classList.remove('is-active');
            overlay.classList.remove('is-active');
            document.body.classList.remove('no-scroll');
        };

        overlay.onclick = closeNav;

        // Mobile Submenu Toggle logic
        navList.addEventListener('click', (e) => {
            const li = e.target.closest('li');
            if (window.innerWidth <= 1024 && li && (li.classList.contains('has-child') || li.classList.contains('has-sub-child'))) {
                // Only toggle if clicking the parent (not a deep link or if we want to prevent default)
                if (e.target.tagName === 'A' && e.target.getAttribute('href') === '#') {
                    e.preventDefault();
                }
                li.classList.toggle('is-toggled');
            }
        });
    }

    const fadeInElements = document.querySelectorAll('.fade-in-element');
    const observer = new IntersectionObserver((entries) => {
        entries.forEach(entry => {
            if (entry.isIntersecting) {
                entry.target.classList.add('is-visible');
                observer.unobserve(entry.target);
            }
        });
    }, { threshold: 0.1 });
    fadeInElements.forEach(el => observer.observe(el));

    // Ajax Load More
    const postWrapper = document.getElementById('post-wrapper');
    const pagerWrapper = document.getElementById('blog-pager');

    if (postWrapper && pagerWrapper) {
        document.addEventListener('click', async (e) => {
            const loadMoreBtn = e.target.closest('.load-more');
            if (loadMoreBtn) {
                e.preventDefault();
                const nextUrl = loadMoreBtn.href;

                loadMoreBtn.textContent = 'Đang tải...';
                loadMoreBtn.style.pointerEvents = 'none';
                loadMoreBtn.style.opacity = '0.7';

                try {
                    const response = await fetch(nextUrl);
                    const html = await response.text();
                    const parser = new DOMParser();
                    const doc = parser.parseFromString(html, 'text/html');

                    // Get new posts
                    const newPosts = doc.querySelectorAll('#post-wrapper .post-card');

                    newPosts.forEach(post => {
                        post.classList.add('fade-in-element');
                        // Chèn bài đăng mới vào ngay TRƯỚC thanh điều hướng (pager)
                        // Điều này đảm bảo nút Xem thêm luôn nằm ở cuối danh sách
                        pagerWrapper.insertAdjacentElement('beforebegin', post);
                        observer.observe(post);
                    });

                    // Get new pager
                    const newPager = doc.getElementById('blog-pager');
                    if (newPager && newPager.querySelector('.load-more')) {
                        pagerWrapper.innerHTML = newPager.innerHTML;
                    } else {
                        pagerWrapper.remove(); // Hết bài đăng thì xóa luôn thanh điều hướng
                    }

                } catch (error) {
                    console.error('Error loading more posts:', error);
                    loadMoreBtn.textContent = 'Thử lại';
                    loadMoreBtn.style.pointerEvents = '';
                    loadMoreBtn.style.opacity = '';
                }
            }
        });
    }

    handleScroll();
});
//
